mapper.update:
 UPDATE MEMBER
 SET M_NICK = #{ m_Nick },
 	 M_ADDRESS = #{ m_Address },
	 M_PHONE = #{ m_Phone }
 WHERE M_NO = #{ m_No }

mapper.select:
		SELECT * FROM BOARD_COMMENT
        WHERE BC_MNO = #{ m_No }
        ORDER BY BC_DATE DESC

